package demo;

public class Upper_to_lower {

			public static void main(String[] args) {
				// TODO Auto-generated method stub
		        String s1="I AM STUDYING IN CHAIATANYA ENGINEERING COLLEGE";
		       System.out.println("original sentence :"+s1);
		        String s2=s1.toLowerCase();
		        System.out.println("After modifying into lower sentence :"+s2);
		        String s4=s1.replace("IN","at");
		        System.out.println("After replacing :"+s4);

		 	}

		}

 
